import { useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
import SectionJobsSidebar1 from "../../sections/jobs/sidebar/section-jobs-sidebar1";
import SectionRecordsFilter from "../../sections/common/section-records-filter";
import SectionPagination from "../../sections/common/section-pagination";
import axios from "axios";

function CandidateGridPage() {
  const [candidates, setCandidates] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [candidatesPerPage, setCandidatesPerPage] = useState(10);

  const _filterConfig = {
    prefix: "Showing",
    type: "Candidates",
    total: candidates.length,
    showRange: false,
    showingUpto: "",
  };

  useEffect(() => {
    axios
      .get("http://localhost:8080/candidate")
      .then(({ data }) => Array.isArray(data) ? setCandidates(data) : setCandidates([]))
      .catch(() => setCandidates([]));
  }, []);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  const currentCandidates = candidates.slice(
    (currentPage - 1) * candidatesPerPage,
    currentPage * candidatesPerPage
  );

  return (
    <div className="section-full p-t120 p-b90 site-bg-white">
      <div className="container">
        <div className="row">
          <div className="col-lg-4 col-md-12">
            <SectionJobsSidebar1 />
          </div>
          <div className="col-lg-8 col-md-12">
            <SectionRecordsFilter
              _config={_filterConfig}
              onCandidatesPerPageChange={(count) => {
                setCandidatesPerPage(count);
                setCurrentPage(1);
              }}
            />
            <div className="twm-candidates-grid-wrap">
              <div className="row">
                {currentCandidates.length > 0 ? (
                  currentCandidates.map(({ _id, Profileimage, Qualification, Username, Jobcategory, Country, Expectedsalery }) => (
                    <div className="col-lg-6 col-md-6" key={_id}>
                      <NavLink to={`/can-detail/${_id}`} className="twm-candidates-grid-style1 mb-5">
                        <div className="twm-media">
                          <img
                            src={Profileimage ? `http://localhost:8080/${Profileimage}` : "/images/default-profile.jpg"}
                            alt={Username}
                            style={{ objectFit: "contain" }}
                          />
                          {Qualification && <span className="twm-candidates-tag">Featured</span>}
                        </div>
                        <div className="twm-mid-content">
                          <h4>{Username || "N/A"}</h4>
                          <p>{Jobcategory || "No category"}</p>
                        </div>
                        <div className="twm-fot-content">
                          <p><i className="feather-map-pin" /> {Country || "Not specified"}</p>
                          <p>{Expectedsalery || "$0"} / Month</p>
                        </div>
                      </NavLink>
                    </div>
                  ))
                ) : (
                  <p>No candidates available</p>
                )}
              </div>
            </div>
            <SectionPagination
              totalItems={candidates.length}
              perPage={candidatesPerPage}
              currentPage={currentPage}
              paginate={paginate}
            />
          </div>
        </div>
      </div>
    </div>
  );
}

export default CandidateGridPage;
