import React, { useEffect, useState } from "react";
import JobZImage from "../../../../common/jobz-img";

function EmpManageJobsPage() {
    const [jobs, setJobs] = useState([]); // State to store job data
    const [loading, setLoading] = useState(true); // State to manage loading state
    const [error, setError] = useState(null); // State to handle errors

    // Fetch job data from the API
    useEffect(() => {
        const fetchJobs = async () => {
            try {
                const response = await fetch("http://localhost:8080/employer/jobs", {
                    method: "GET",
                    credentials: "include", // Include cookies in the request
                });

                if (!response.ok) {
                    throw new Error("Failed to fetch jobs");
                }

                const data = await response.json();

                if (data.success) {
                    setJobs(data.data); // Set the fetched jobs to state
                } else {
                    setError("Failed to fetch jobs");
                }
            } catch (err) {
                console.error("Error fetching jobs:", err);
                setError("An error occurred while fetching jobs");
            } finally {
                setLoading(false); // Set loading to false after the request completes
            }
        };

        fetchJobs();
    }, []);

    // Display loading state
    if (loading) {
        return <div>Loading...</div>;
    }

    // Display error state
    if (error) {
        return <div>{error}</div>;
    }

    return (
        <>
            <div className="wt-admin-right-page-header clearfix">
                <h2>Manage Jobs</h2>
                <div className="breadcrumbs">
                    <a href="#">Home</a>
                    <a href="#">Dashboard</a>
                    <span>My Job Listing</span>
                </div>
            </div>

            {/* Job Details Table */}
            <div className="panel panel-default">
                <div className="panel-heading wt-panel-heading p-a20">
                    <h4 className="panel-tittle m-a0">
                        <i className="fa fa-suitcase" /> Job Details
                    </h4>
                </div>
                <div className="panel-body wt-panel-body p-a20 m-b30 ">
                    <div className="twm-D_table p-a20 table-responsive">
                        <table id="jobs_bookmark_table" className="table table-bordered twm-bookmark-list-wrap">
                            <thead>
                                <tr>
                                    <th>Job Title</th>
                                    <th>Category</th>
                                    <th>Job Types</th>
                                    <th>Applications</th>
                                    <th>Created &amp; Expired</th>
                                    <th>Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                {jobs.map((job) => (
                                    <tr key={job._id}>
                                        <td>
                                            <div className="twm-bookmark-list">
                                                <div className="twm-media">
                                                    <div className="twm-media-pic">
                                                        <JobZImage src="images/jobs-company/pic1.jpg" alt="#" />
                                                    </div>
                                                </div>
                                                <div className="twm-mid-content">
                                                    <a href="#" className="twm-job-title">
                                                        <h4>{job.jobtitle}</h4>
                                                        <p className="twm-bookmark-address">
                                                            <i className="feather-map-pin" />
                                                            {job.location}
                                                        </p>
                                                    </a>
                                                </div>
                                            </div>
                                        </td>
                                        <td>{job.jobcategory}</td>
                                        <td>
                                            <div className="twm-jobs-category">
                                                <span className={`twm-bg-green`}>{job.jobtype}</span>
                                            </div>
                                        </td>
                                        <td>
                                            <a href="#" className="site-text-primary">
                                                {job.applications || "0"} Applied
                                            </a>
                                        </td>
                                        <td>
                                            <div>{new Date(job.startDate).toLocaleDateString()}</div>
                                            <div>{new Date(job.endDate).toLocaleDateString()}</div>
                                        </td>
                                        <td>
                                            <div className="twm-table-controls">
                                                <ul className="twm-DT-controls-icon list-unstyled">
                                                    <li>
                                                        <button title="View profile" data-bs-toggle="tooltip" data-bs-placement="top">
                                                            <span className="fa fa-eye" />
                                                        </button>
                                                    </li>
                                                    <li>
                                                        <button title="Edit" data-bs-toggle="tooltip" data-bs-placement="top">
                                                            <span className="far fa-edit" />
                                                        </button>
                                                    </li>
                                                    <li>
                                                        <button title="Delete" data-bs-toggle="tooltip" data-bs-placement="top">
                                                            <span className="far fa-trash-alt" />
                                                        </button>
                                                    </li>
                                                </ul>
                                            </div>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                            <tfoot>
                                <tr>
                                    <th>Job Title</th>
                                    <th>Category</th>
                                    <th>Job Types</th>
                                    <th>Applications</th>
                                    <th>Created &amp; Expired</th>
                                    <th>Action</th>
                                </tr>
                            </tfoot>
                        </table>
                    </div>
                </div>
            </div>
        </>
    );
}

export default EmpManageJobsPage;
