import React, { useEffect, useState } from "react";
import JobZImage from "../../../../common/jobz-img";

function EmpManageJobsPage() {
    const [applications, setApplications] = useState([]); // State to store applications data
    const [loading, setLoading] = useState(true); // State to manage loading state
    const [error, setError] = useState(null); // State to handle errors

    // Fetch applications data from the API
    useEffect(() => {
        const fetchApplications = async () => {
            try {
                const response = await fetch("/employer/applications", {
                    method: "GET",
                    credentials: "include", // Include cookies in the request
                });

                if (!response.ok) {
                    throw new Error("Failed to fetch applications");
                }

                const data = await response.json();

                if (data.success) {
                    setApplications(data.data); // Set the fetched applications to state
                } else {
                    setError("Failed to fetch applications");
                }
            } catch (err) {
                console.error("Error fetching applications:", err);
                setError("An error occurred while fetching applications");
            } finally {
                setLoading(false); // Set loading to false after the request completes
            }
        };

        fetchApplications();
    }, []);

    // Display loading state
    if (loading) {
        return <div>Loading...</div>;
    }

    // Display error state
    if (error) {
        return <div>{error}</div>;
    }

    return (
        <>
            <div className="wt-admin-right-page-header clearfix">
                <h2>Manage Jobs</h2>
                <div className="breadcrumbs">
                    <a href="#">Home</a>
                    <a href="#">Dashboard</a>
                    <span>My Job Listing</span>
                </div>
            </div>

            {/* Job Details Table */}
            <div className="panel panel-default">
                <div className="panel-heading wt-panel-heading p-a20">
                    <h4 className="panel-tittle m-a0">
                        <i className="fa fa-suitcase" /> Job Applications
                    </h4>
                </div>
                <div className="panel-body wt-panel-body p-a20 m-b30 ">
                    <div className="twm-D_table p-a20 table-responsive">
                        <table id="jobs_bookmark_table" className="table table-bordered twm-bookmark-list-wrap">
                            <thead>
                                <tr>
                                    <th>Candidate Name</th>
                                    <th>Job Title</th>
                                    <th>Category</th>
                                    <th>Email</th>
                                    <th>Phone</th>
                                    <th>Message</th>
                                    <th>Resume</th>
                                    <th>Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                {applications.map((application) => (
                                    <tr key={application._id}>
                                        <td>
                                            <div className="twm-bookmark-list">
                                                <div className="twm-media">
                                                    <div className="twm-media-pic">
                                                        <JobZImage src="images/jobs-company/pic1.jpg" alt="#" />
                                                    </div>
                                                </div>
                                                <div className="twm-mid-content">
                                                    <a href="#" className="twm-job-title">
                                                        <h4>{application.userId.name}</h4>
                                                        <p className="twm-bookmark-address">
                                                            <i className="feather-map-pin" />
                                                            {application.JobId.location}
                                                        </p>
                                                    </a>
                                                </div>
                                            </div>
                                        </td>
                                        <td>{application.JobId.jobtitle}</td>
                                        <td>{application.JobId.jobcategory}</td>
                                        <td>{application.email}</td>
                                        <td>{application.phone}</td>
                                        <td>{application.message}</td>
                                        <td>
                                            {application.resume ? (
                                                <a href={application.resume} target="_blank" rel="noopener noreferrer" className="site-text-primary">
                                                    View Resume
                                                </a>
                                            ) : (
                                                "No Resume"
                                            )}
                                        </td>
                                        <td>
                                            <div className="twm-table-controls">
                                                <ul className="twm-DT-controls-icon list-unstyled">
                                                    <li>
                                                        <button title="View profile" data-bs-toggle="tooltip" data-bs-placement="top">
                                                            <span className="fa fa-eye" />
                                                        </button>
                                                    </li>
                                                    <li>
                                                        <button title="Edit" data-bs-toggle="tooltip" data-bs-placement="top">
                                                            <span className="far fa-edit" />
                                                        </button>
                                                    </li>
                                                    <li>
                                                        <button title="Delete" data-bs-toggle="tooltip" data-bs-placement="top">
                                                            <span className="far fa-trash-alt" />
                                                        </button>
                                                    </li>
                                                </ul>
                                            </div>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                            <tfoot>
                                <tr>
                                    <th>Candidate Name</th>
                                    <th>Job Title</th>
                                    <th>Category</th>
                                    <th>Email</th>
                                    <th>Phone</th>
                                    <th>Message</th>
                                    <th>Resume</th>
                                    <th>Action</th>
                                </tr>
                            </tfoot>
                        </table>
                    </div>
                </div>
            </div>
        </>
    );
}

export default EmpManageJobsPage;
