import { NavLink, useNavigate } from "react-router-dom";
import JobZImage from "../../../../common/jobz-img";
import { canRoute, candidate, empRoute, employer, publicUser } from "../../../../../globals/route-names";
import { useState } from "react";
import processLogin from "../../../../form-processing/login";
import { formType } from "../../../../../globals/constants";

function AfterLoginPage() {
    const navigate = useNavigate();
    const [canusername, setCanUsername] = useState('guest');
    const [empusername, setEmpUsername] = useState('admin');
    const [password, setPassword] = useState('12345');

    const handleCandidateLogin = (event) => {
        event.preventDefault();
        loginCandidate();
    };

    const handleEmployerLogin = (event) => {
        event.preventDefault();
        loginEmployer();
    };

    const loginCandidate = () => {
        processLogin(
            {
                type: formType.LOGIN_CANDIDATE,
                username: canusername,
                password: password,
            },
            (valid) => {
                if (valid) {
                    moveToCandidate();
                } else {
                    // Show error
                    console.log('error');
                }
            }
        );
    };

    const loginEmployer = () => {
        processLogin(
            {
                type: formType.LOGIN_EMPLOYER,
                username: empusername,
                password: password,
            },
            (valid) => {
                if (valid) {
                    moveToEmployer();
                } else {
                    // Show error
                    console.log('error');
                }
            }
        );
    };

    const moveToCandidate = () => {
        navigate(canRoute(candidate.DASHBOARD));
    };

    const moveToEmployer = () => {
        navigate(empRoute(employer.DASHBOARD));
    };

    return (
        <div
            style={{
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                minHeight: '100vh',
                backgroundColor: '#f8f9fa',
                padding: '20px',
            }}
        >
            <div
                style={{
                    width: '90%',
                    maxWidth: '1200px',
                    backgroundColor: '#fff',
                    borderRadius: '40px',
                    boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)',
                    display: 'flex',
                    flexDirection: 'row',
                    overflow: 'hidden',
                    border: '10px solid #1967d2',
                }}
            >
                {/* Left Side: Image */}
                <div
                    style={{
                        flex: 1,
                        display: 'flex',
                        justifyContent: 'center',
                        alignItems: 'center',
                        backgroundColor: '#1967d2',
                        padding: '20px',
                    }}
                >
                    <img
                        src="images/login1.png"
                        alt="Login"
                        style={{
                            width: '100%',
                            height: 'auto',
                            borderRadius: '20px',
                        }}
                    />
                </div>

                {/* Right Side: Login Form */}
                <div
                    style={{
                        flex: 1,
                        padding: '40px',
                        display: 'flex',
                        flexDirection: 'column',
                        justifyContent: 'center',
                    }}
                >
                    <div style={{ textAlign: 'center', marginBottom: '20px' }}>
                        <NavLink to={publicUser.HOME1}>
                            <img
                                src="/assets/images/skins-logo/logo-skin-6-ftr.png"
                                alt="Logo"
                                style={{ width: '150px', height: 'auto' }}
                            />
                        </NavLink>
                    </div>
                    <div style={{ marginBottom: '20px', textAlign: 'center' }}>
                        <span style={{ fontSize: '24px', fontWeight: 'bold' }}>Log In</span>
                    </div>
                    <div>
                        <ul
                            style={{
                                display: 'flex',
                                justifyContent: 'center',
                                listStyle: 'none',
                                padding: 0,
                                marginBottom: '20px',
                            }}
                        >
                            <li style={{ margin: '0 10px' }}>
                                <button
                                    style={{
                                        padding: '10px 20px',
                                        border: 'none',
                                        backgroundColor: '#1967d2',
                                        color: '#fff',
                                        borderRadius: '5px',
                                        cursor: 'pointer',
                                    }}
                                >
                                    <i className="fas fa-user-tie" /> Candidate
                                </button>
                            </li>
                            <li style={{ margin: '0 10px' }}>
                                <button
                                    style={{
                                        padding: '10px 20px',
                                        border: 'none',
                                        backgroundColor: '#1967d2',
                                        color: '#fff',
                                        borderRadius: '5px',
                                        cursor: 'pointer',
                                    }}
                                >
                                    <i className="fas fa-building" /> Employer
                                </button>
                            </li>
                        </ul>
                        <div>
                            {/* Candidate Login Form */}
                            <form onSubmit={handleCandidateLogin}>
                                <div style={{ marginBottom: '15px' }}>
                                    <input
                                        type="text"
                                        placeholder="Username*"
                                        value={canusername}
                                        onChange={(e) => setCanUsername(e.target.value)}
                                        style={{
                                            width: '100%',
                                            padding: '10px',
                                            borderRadius: '5px',
                                            border: '1px solid #ddd',
                                        }}
                                    />
                                </div>
                                <div style={{ marginBottom: '15px' }}>
                                    <input
                                        type="password"
                                        placeholder="Password*"
                                        value={password}
                                        onChange={(e) => setPassword(e.target.value)}
                                        style={{
                                            width: '100%',
                                            padding: '10px',
                                            borderRadius: '5px',
                                            border: '1px solid #ddd',
                                        }}
                                    />
                                </div>
                                <div style={{ marginBottom: '15px' }}>
                                    <label style={{ display: 'flex', alignItems: 'center' }}>
                                        <input
                                            type="checkbox"
                                            style={{ marginRight: '10px' }}
                                        />
                                        Remember me
                                    </label>
                                </div>
                                <button
                                    type="submit"
                                    style={{
                                        width: '100%',
                                        padding: '10px',
                                        backgroundColor: '#1967d2',
                                        color: '#fff',
                                        border: 'none',
                                        borderRadius: '5px',
                                        cursor: 'pointer',
                                    }}
                                >
                                    Log in
                                </button>
                            </form>

                            {/* Employer Login Form */}
                            <form onSubmit={handleEmployerLogin} style={{ marginTop: '20px' }}>
                                <div style={{ marginBottom: '15px' }}>
                                    <input
                                        type="text"
                                        placeholder="Username*"
                                        value={empusername}
                                        onChange={(e) => setEmpUsername(e.target.value)}
                                        style={{
                                            width: '100%',
                                            padding: '10px',
                                            borderRadius: '5px',
                                            border: '1px solid #ddd',
                                        }}
                                    />
                                </div>
                                <div style={{ marginBottom: '15px' }}>
                                    <input
                                        type="password"
                                        placeholder="Password*"
                                        value={password}
                                        onChange={(e) => setPassword(e.target.value)}
                                        style={{
                                            width: '100%',
                                            padding: '10px',
                                            borderRadius: '5px',
                                            border: '1px solid #ddd',
                                        }}
                                    />
                                </div>
                                <div style={{ marginBottom: '15px' }}>
                                    <label style={{ display: 'flex', alignItems: 'center' }}>
                                        <input
                                            type="checkbox"
                                            style={{ marginRight: '10px' }}
                                        />
                                        Remember me
                                    </label>
                                </div>
                                <button
                                    type="submit"
                                    style={{
                                        width: '100%',
                                        padding: '10px',
                                        backgroundColor: '#1967d2',
                                        color: '#fff',
                                        border: 'none',
                                        borderRadius: '5px',
                                        cursor: 'pointer',
                                    }}
                                >
                                    Log in
                                </button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default AfterLoginPage;
