import React from "react";

function SectionPagination({ totalItems, perPage, currentPage, paginate }) {
  const totalPages = Math.ceil(totalItems / perPage);
  const pageNumbers = Array.from({ length: totalPages }, (_, i) => i + 1);

  return (
    <div className="pagination-outer">
      <div className="pagination-style1">
        <ul className="clearfix">
          {/* Previous Button */}
          <li className={`prev ${currentPage === 1 ? "disabled" : ""}`}>
            <button
              disabled={currentPage === 1}
              onClick={() => currentPage > 1 && paginate(currentPage - 1)}
            >
              <span>
                <i className="fa fa-angle-left" />
              </span>
            </button>
          </li>

          {/* Page Numbers */}
          {pageNumbers.map((pageNumber) => (
            <li
              key={pageNumber}
              className={currentPage === pageNumber ? "active" : ""}
            >
              <button onClick={() => paginate(pageNumber)}>{pageNumber}</button>
            </li>
          ))}

          {/* Next Button */}
          <li className={`next ${currentPage === totalPages ? "disabled" : ""}`}>
            <button
              disabled={currentPage === totalPages}
              onClick={() => currentPage < totalPages && paginate(currentPage + 1)}
            >
              <span>
                <i className="fa fa-angle-right" />
              </span>
            </button>
          </li>
        </ul>
      </div>
    </div>
  );
}

export default SectionPagination;
