import React, { useEffect, useState } from "react";
import axios from "axios";
import SectionRecordsFilter from "../../sections/common/section-records-filter";
import SectionPagination from "../../sections/common/section-pagination";
import SectionJobsSidebar1 from "../../sections/jobs/sidebar/section-jobs-sidebar1";

function CandidateGridPage() {
  const [candidates, setCandidates] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [candidatesPerPage, setCandidatesPerPage] = useState(10);
  const [error, setError] = useState(null);

  const _filterConfig = {
    prefix: "Showing",
    type: "Candidates",
    total: candidates.length,
    showRange: false,
    showingUpto: "",
  };

  const fetchCandidates = async () => {
    try {
      const { data } = await axios.get("http://localhost:8080/candidate");
      if (!Array.isArray(data)) {
        throw new Error("Unexpected API response format.");
      }
      setCandidates(data);
    } catch (err) {
      setError(err.message || "Error fetching candidates.");
    }
  };

  useEffect(() => {
    fetchCandidates();
  }, []);

  const indexOfLastCandidate = currentPage * candidatesPerPage;
  const indexOfFirstCandidate = indexOfLastCandidate - candidatesPerPage;
  const currentCandidates = candidates.slice(indexOfFirstCandidate, indexOfLastCandidate);

  return (
    <div className="section-full p-t120 p-b90 site-bg-white">
      <div className="container">
        <div className="row">
          <div className="col-lg-4 col-md-12">
            <SectionJobsSidebar1 />
          </div>
          <div className="col-lg-8 col-md-12">
            <SectionRecordsFilter
              _config={_filterConfig}
              onCandidatesPerPageChange={(count) => {
                setCandidatesPerPage(count);
                setCurrentPage(1);
              }}
            />
            <div className="twm-candidates-grid-wrap">
              {error ? (
                <p className="text-danger">{error}</p>
              ) : currentCandidates.length > 0 ? (
                <div className="row">
                  {currentCandidates.map((candidate) => (
                    <div className="col-lg-6 col-md-6" key={candidate._id}>
                      <div className="twm-candidates-grid-style1 mb-5">
                        <div className="twm-media">
                          <img
                            src={
                              candidate.Profileimage
                                ? `http://localhost:8080/${candidate.Profileimage}`
                                : "/images/default-profile.jpg"
                            }
                            alt={candidate.Username || "Candidate"}
                            style={{ objectFit: "contain" }}
                          />
                        </div>
                        <div className="twm-mid-content">
                          <h4>{candidate.Username || "Anonymous"}</h4>
                          <p>{candidate.Jobcategory || "No category specified"}</p>
                        </div>
                        <div className="twm-right-content">
                          <p>{candidate.Country || "Unknown location"}</p>
                          <p>
                            {candidate.Expectedsalery || "N/A"} {candidate.rateType || "/ Month"}
                          </p>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <p>No candidates found.</p>
              )}
            </div>
            <SectionPagination
              totalItems={candidates.length}
              perPage={candidatesPerPage}
              currentPage={currentPage}
              paginate={setCurrentPage}
            />
          </div>
        </div>
      </div>
    </div>
  );
}

export default CandidateGridPage;
