import SectionJobsSidebar1 from "../../sections/jobs/sidebar/section-jobs-sidebar1";
import SectionJobsList from "../../sections/jobs/section-jobs-list";
import SectionRecordsFilter from "../../sections/common/section-records-filter";
import { useEffect, useState } from "react";
import { loadScript } from "../../../../../globals/constants";
import axios from "axios";

function JobsListPage() {
    const [jobs, setJobs] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [jobsPerPage, setJobsPerPage] = useState(10);

    const _filterConfig = {
        prefix: "Showing",
        type: "jobs",
        total: jobs.length,
        showRange: false,
        showingUpto: "",
    };

    useEffect(() => {
        loadScript("js/custom.js");

        // Fetch jobs from the API
        axios.get("http://localhost:8080/post-job")
            .then((response) => {
                setJobs(response.data);
            })
            .catch((error) => console.error("Error fetching job data", error));
    }, []);

    // Pagination logic
    const indexOfLastJob = currentPage * jobsPerPage;
    const indexOfFirstJob = indexOfLastJob - jobsPerPage;
    const currentJobs = jobs.slice(indexOfFirstJob, indexOfLastJob);

    const paginate = (pageNumber) => setCurrentPage(pageNumber);

    const handleJobsPerPageChange = (event) => {
        setJobsPerPage(Number(event.target.value));
        setCurrentPage(1); // Reset to the first page
    };

    return (
        <div className="section-full p-t120 p-b90 site-bg-white">
            <div className="container">
                <div className="row">
                    <div className="col-lg-4 col-md-12 rightSidebar">
                        <SectionJobsSidebar1 />
                    </div>
                    <div className="col-lg-8 col-md-12">
                        <SectionRecordsFilter
                            _config={_filterConfig}
                            onCandidatesPerPageChange={handleJobsPerPageChange}
                        />
                        <SectionJobsList
                            jobs={currentJobs}
                            currentPage={currentPage}
                            perPage={jobsPerPage}
                            totalItems={jobs.length}
                            paginate={paginate}
                        />
                    </div>
                </div>
            </div>
        </div>
    );
}

export default JobsListPage;
