import { useState, useEffect } from "react";
import { NavLink } from "react-router-dom";
import { publicUser } from "../../../../../../globals/route-names";
import SectionSideAdvert from "./section-side-advert";

function SectionJobsSidebar1() {
    const [jobcategory, setJobcategory] = useState("");
    const [jobtype, setJobtype] = useState("");
    const [city, setCity] = useState("");
    const [categories, setCategories] = useState([]);
    const [locations, setLocations] = useState([]);
    const [jobs, setJobs] = useState([]);

    // Fetch job categories and cities using fetch
    useEffect(() => {
        const fetchCategories = async () => {
            try {
                const response = await fetch("http://localhost:8080/api/job-categories");
                const data = await response.json();
                setCategories(data); // Set categories state
            } catch (error) {
                console.error("Error fetching categories:", error);
            }
        };

        const fetchCities = async () => {
            try {
                const response = await fetch("http://localhost:8080/api/cities");
                const data = await response.json();
                setLocations(data); // Set cities state
            } catch (error) {
                console.error("Error fetching cities:", error);
            }
        };

        fetchCategories();
        fetchCities();
    }, []);

    const handleSearch = async () => {
        try {
            const response = await fetch("http://localhost:8080/api/search-jobs", {
                method: "GET",
                headers: {
                    "Content-Type": "application/json",
                },
                // Pass parameters as query strings
                body: JSON.stringify({
                    jobcategory,
                    jobtype,
                    city,
                }),
            });
            const data = await response.json();
            setJobs(data); // Set the jobs state with the fetched data
        } catch (error) {
            console.error("Error fetching jobs:", error);
        }
    };

    console.log(locations);

    return (
        <>
            <div className="side-bar">
                <div className="sidebar-elements search-bx">
                    <form>
                        {/* Job Category Dropdown */}
                        <div className="form-group mb-4">
                            <h4 className="section-head-small mb-4">Category</h4>
                            <select
                                className="wt-select-bar-large selectpicker"
                                onChange={(e) => setJobcategory(e.target.value)}
                                value={jobcategory}
                            >
                                <option value="">All Categories</option>
                                {categories.map((category, index) => (
                                    <option key={index} value={category}>
                                        {category}
                                    </option>
                                ))}
                            </select>
                        </div>

                        {/* Job Type Dropdown */}
                        <div className="form-group mb-4">
                            <h4 className="section-head-small mb-4">Job Type</h4>
                            <select
                                className="wt-select-bar-large selectpicker"
                                onChange={(e) => setJobtype(e.target.value)}
                                value={jobtype}
                            >
                                <option value="">All Types</option>
                                <option value="Full Time">Full Time</option>
                                <option value="Part Time">Part Time</option>
                                <option value="Freelance">Freelance</option>
                                <option value="Internship">Internship</option>
                            </select>
                        </div>

                        {/* Location Input */}
                        <div className="form-group mb-4">
                            <h4 className="section-head-small mb-4">Location</h4>
                            <select
                                className="wt-select-bar-large selectpicker"
                                onChange={(e) => setCity(e.target.value)}
                                value={city}
                            >
                                <option value="">All Locations</option>
                                {locations.map((location, index) => (
                                    <option key={index} value={location}>
                                        {location}
                                    </option>
                                ))}
                            </select>
                        </div>

                        {/* Search Button */}
                        <div className="form-group mb-4">
                            <button
                                type="button"
                                className="btn btn-primary"
                                onClick={handleSearch}
                            >
                                Search
                            </button>
                        </div>
                    </form>
                </div>
            </div>

            {/* Display the filtered job results */}
            <div className="job-results">
                {jobs.length > 0 ? (
                    jobs.map((job) => (
                        <div key={job._id} className="job-item">
                            <h5>{job.jobtitle}</h5>
                            <p>{job.jobcategory}</p>
                            <p>{job.city}</p>
                            {/* Add more job details */}
                        </div>
                    ))
                ) : (
                    <p>No jobs found matching the search criteria</p>
                )}
            </div>
            <SectionSideAdvert />
        </>
    );
}

export default SectionJobsSidebar1;
