import { NavLink } from "react-router-dom";
import SectionJobsSidebar1 from "../../sections/jobs/sidebar/section-jobs-sidebar1";
import SectionRecordsFilter from "../../sections/common/section-records-filter";
import SectionPagination from "../../sections/common/section-pagination";
import { useEffect, useState } from "react";
import axios from "axios";

function CandidateGridPage() {
  const _filterConfig = {
    prefix: "Showing",
    type: "Candidates",
    total: "2,150",
    showRange: false,
    showingUpto: "",
  };

  const [candidates, setCandidates] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [candidatesPerPage, setCandidatesPerPage] = useState(10);

  useEffect(() => {
    axios
      .get("http://localhost:8080/candidate")
      .then((response) => {
        if (Array.isArray(response.data)) {
          setCandidates(response.data);
        } else {
          console.error("Response data is not an array");
        }
      })
      .catch((error) => console.error("Error fetching candidates:", error));
  }, []);

  const indexOfLastCandidate = currentPage * candidatesPerPage;
  const indexOfFirstCandidate = indexOfLastCandidate - candidatesPerPage;
  const currentCandidates = candidates.slice(indexOfFirstCandidate, indexOfLastCandidate);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  const handleCandidatesPerPageChange = (event) => {
    setCandidatesPerPage(Number(event.target.value));
    setCurrentPage(1);
  };

  return (
    <div className="section-full p-t120 p-b90 site-bg-white">
      <div className="container">
        <div className="row">
          <div className="col-lg-4 col-md-12 rightSidebar">
            <SectionJobsSidebar1 />
          </div>
          <div className="col-lg-8 col-md-12">
            <SectionRecordsFilter
              _config={_filterConfig}
              onCandidatesPerPageChange={handleCandidatesPerPageChange}
            />
            <div className="twm-candidates-grid-wrap">
              <div className="row">
                {currentCandidates.length > 0 ? (
                  currentCandidates.map((candidate) => (
                    <div
                      className="col-lg-6 col-md-6 mb-4"
                      key={candidate._id}
                    >
                      <NavLink
                        to={`/can-detail/${candidate._id}`}
                        className="candidate-card shadow-lg rounded p-3 h-100"
                      >
                        <div className="candidate-media mb-3">
                          <img
                            src={`http://localhost:8080/${candidate.Profileimage}`}
                            onError={(e) =>
                              (e.target.src = "/images/default-profile.jpg")
                            }
                            alt={candidate.Username || "Candidate"}
                            className="img-fluid rounded"
                            style={{
                              width: "100%",
                              height: "180px",
                              objectFit: "cover",
                            }}
                          />
                        </div>
                        <div className="candidate-info">
                          <h4 className="mb-1 text-dark">
                            {candidate.Username || "N/A"}
                          </h4>
                          <p className="text-muted small mb-2">
                            {candidate.Jobcategory || "No category"}
                          </p>
                          <p className="text-muted small mb-2">
                            <i className="feather-map-pin me-1" />
                            {candidate.Country || "Not specified"}
                          </p>
                          <p className="text-muted small">
                            {candidate.Expectedsalery || "$0"} / Month
                          </p>
                        </div>
                      </NavLink>
                    </div>
                  ))
                ) : (
                  <p>No candidates available</p>
                )}
              </div>
            </div>
            <SectionPagination
              totalItems={candidates.length}
              perPage={candidatesPerPage}
              currentPage={currentPage}
              paginate={paginate}
            />
          </div>
        </div>
      </div>
    </div>
  );
}

export default CandidateGridPage;
