import SectionJobsSidebar1 from "../../sections/jobs/sidebar/section-jobs-sidebar1";
import SectionJobsGrid from "../../sections/jobs/section-jobs-grid";
import SectionRecordsFilter from "../../sections/common/section-records-filter";
import { useEffect, useState } from "react";
import { loadScript } from "../../../../../globals/constants";

function JobsGridPage() {
  const [perPage, setPerPage] = useState(10);
  const [category, setCategory] = useState(""); // State for category filter
  const [location, setLocation] = useState(""); // State for location filter
  const [keyword, setKeyword] = useState(""); // State for keyword filter

  useEffect(() => {
    loadScript("js/custom.js");
  }, []);

  return (
    <div className="section-full p-t120 p-b90 site-bg-white">
      <div className="container">
        <div className="row">
          <div className="col-lg-4 col-md-12 rightSidebar">
            <SectionJobsSidebar1
              setCategory={setCategory}
              setLocation={setLocation}
              setKeyword={setKeyword}
            />
          </div>
          <div className="col-lg-8 col-md-12">
            <SectionJobsGrid
              perPage={perPage}
              category={category}
              location={location}
              keyword={keyword}
            />
          </div>
        </div>
      </div>
    </div>
  );
}


export default JobsGridPage;
