import { NavLink, useNavigate } from "react-router-dom";
import JobZImage from "../../../../common/jobz-img";
import { canRoute, candidate, empRoute, employer, publicUser } from "../../../../../globals/route-names";
import { useState } from "react";
import processLogin from "../../../../form-processing/login";
import { formType } from "../../../../../globals/constants";

function AfterLoginPage() {

    const navigate = useNavigate();
    const [canusername, setCanUsername] = useState('guest');
    const [empusername, setEmpUsername] = useState('admin');
    const [password, setPassword] = useState('12345');

    const handleCandidateLogin = (event) => {
        event.preventDefault();
        loginCandidate();
    }

    const handleEmployerLogin = (event) => {
        event.preventDefault();
        loginEmployer();
    }

    const loginCandidate = () => {
        processLogin(
            {
                type: formType.LOGIN_CANDIDATE,
                username: canusername,
                password: password
            },
            (valid) => {
                if (valid) {
                    moveToCandidate();
                } else {
                    // show error
                    console.log('error');
                }
            }
        );
    }

    const loginEmployer = () => {
        processLogin(
            {
                type: formType.LOGIN_EMPLOYER,
                username: empusername,
                password: password
            },
            (valid) => {
                if (valid) {
                    moveToEmployer();
                } else {
                    // show error
                    console.log('error');
                }
            }
        );
    }

    const moveToCandidate = () => {
        navigate(canRoute(candidate.DASHBOARD));
    }

    const moveToEmployer = () => {
        navigate(empRoute(employer.DASHBOARD));
    }

    return (
        <>
            <div className="loginbg" style={{ display: 'flex', width: '100%', height: '1300px', justifyContent: 'center', alignItems: 'center', boxShadow: 'lg', }}>

                <div className="section-full site-bg-white" style={{ width: '85%', height: '82%', boxShadow: 'lg', backgroundColor: '1967d2', borderRadius: '40px', display: 'flex', justifyContent: 'center', alignItems: 'center', border: '10px solid #1967d2' }}>
                    <div className="container-fluid" style={{ width: '100%', height: '70%', borderRadius: '40px' }}>
                        <div className="row" style={{ backgroundcolor: '#1967d2', width: '100%', height: '100%', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                            <div className="col-xl-6 col-lg-6 col-md-6 twm-log-reg-media-wrap" style={{ height: '100%', width: '38%', marginRight: '10%', borderRadius: '40px' }}>


                                <JobZImage src="images/login1.png" alt="" style={{ height: '100%', width: '100%', borderRadius: '40px', }} />

                            </div>
                            <div className="col-xl-12 col-lg-12 col-md-12" style={{ height: '100%', width: '40%', boxShadow: '20px whitesomke' }}>
                                <div className="twm-log-reg-form-wrap row">
                                    <div className="col-xl-6 col-lg-6 col-md-12 col-12">
                                        <div className="twm-log-reg-logo-head">
                                            <NavLink to={publicUser.HOME1}>
                                                <img src="/assets/images/skins-logo/logo-skin-6-ftr.png" alt="" className="logo" />
                                            </NavLink>
                                        </div>
                                    </div>
                                    <div className="col-xl-6 col-lg-6 col-md-12">

                                        <div className="twm-log-reg-inner">
                                            <div className="twm-log-reg-head">
                                                <div className="twm-log-reg-logo">
                                                    <span className="log-reg-form-title">Log In</span>
                                                </div>
                                            </div>
                                            <div className="twm-tabs-style-2">
                                                <ul className="nav nav-tabs" id="myTab2" role="tablist">
                                                    {/*Login Candidate*/}
                                                    <li className="nav-item">
