import React, { useState, useEffect } from 'react';

function SectionJobLocation() {
    const [location, setLocation] = useState(null);

    useEffect(() => {
        // Fetch the user's location on component mount
        navigator.geolocation.getCurrentPosition(
            (position) => {
                const { latitude, longitude } = position.coords;
                setLocation({ latitude, longitude });
            },
            (error) => {
                console.error("Error getting location: ", error);
                // Fallback to a default location if geolocation fails
                setLocation({ latitude: 34.073270780600225, longitude: -118.2533646842856 }); // Default to Los Angeles
            }
        );
    }, []);

    if (!location) {
        return <div>Loading map...</div>;
    }

    const mapUrl = `https://www.google.com/maps/embed/v1/view?key=AIzaSyC1NmjFs2YpXMKS_h4maHkyi9S6YCCuGL0&center=${location.latitude},${location.longitude}&zoom=12`;

    return (
        <>
            <h4 className="twm-s-title">Location</h4>
            <div className="twm-m-map mb-5">
                <div className="twm-m-map-iframe">
                    <iframe
                        title="Job Location Map"
                        height={310}
                        src={mapUrl}
                        style={{ border: 0 }}
                        allowFullScreen=""
                        loading="lazy"
                    />
                </div>
            </div>
        </>
    );
}

export default SectionJobLocation;
