import React, { useState } from 'react';
import EmpSidebarSection from '../../common/empsidebar';

function EmpPostAJobPage() {
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [formData, setFormData] = useState({
    jobtitle: '',
    jobcategory: '',
    jobtype: '',
    offeredsalary: '',
    experience: '',
    qualification: '',
    gender: '',
    country: '',
    city: '',
    location: '',
    latitude: '',
    longitude: '',
    question: [],
    email: '',
    website: '',
    estsince: '',
    completeaddress: '',
    description: '',
    startDate: '',
    endDate: '',
    Vacnices: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value
    }));
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFormData((prevData) => ({
        ...prevData,
        estsince: file
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.jobtitle || !formData.description || !formData.website) {
      setError('Please fill out all required fields.');
      return;
    }

    try {
      const response = await fetch(`http://localhost:8080/post-job`, {
        method: 'POST',
        credentials: 'include',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(formData)
      });

      const result = await response.json();

      if (result.success) {
        setSuccess('Job posted successfully.');
        setError('');
      } else {
        setError('Failed to post job. Please try again.');
      }
    } catch (error) {
      console.error('Error posting job:', error);
      setError('An error occurred. Please try again.');
    }
  };

  return (
    <>
      <div style={{ display: 'flex', justifyContent: 'center', alignContent: 'center' }}>
        <div style={{ width: '80%' }}>
          <div className="wt-admin-right-page-header clearfix">
            <h2>Post a Course</h2>
            <div className="breadcrumbs">
              <a href="#">Home</a>
              <a href="#">Dashboard</a>
              <span>Course Submission Form</span>
            </div>
          </div>

          <div className="panel panel-default">
            <div className="panel-heading wt-panel-heading p-a20">
              <h4 className="panel-title m-a0">
                <i className="fa fa-suitcase" /> Course Details
              </h4>
            </div>
            <div className="panel-body wt-panel-body p-a20 m-b30">
              <form onSubmit={handleSubmit}>
                <div className="row">
                  {/* Job Title */}
                  <div className="col-xl-6 col-lg-6 col-md-12">
                    <div className="form-group">
                      <label>Course Title</label>
                      <div className="ls-inputicon-box">
                        <input
                          className="form-control"
                          name="jobtitle"
                          value={formData.jobtitle}
                          onChange={handleChange}
                          type="text"
                          placeholder="Enter course title"
                          required
                        />
                        <i className="fs-input-icon fa fa-address-card" />
                      </div>
                    </div>
                  </div>

                  {/* Website */}
                  <div className="col-xl-6 col-lg-6 col-md-12">
                    <div className="form-group">
                      <label>Website</label>
                      <div className="ls-inputicon-box">
                        <input
                          className="form-control"
                          name="website"
                          value={formData.website}
                          onChange={handleChange}
                          type="url"
                          placeholder="https://example.com"
                          required
                        />
                        <i className="fs-input-icon fa fa-globe-americas" />
                      </div>
                    </div>
                  </div>

                  {/* Est. Since */}
                  <div className="col-xl-6 col-lg-6 col-md-12">
                    <div className="form-group">
                      <label>Est. Since</label>
                      <div className="ls-inputicon-box">
                        <input
                          className="form-control"
                          name="estsince"
                          onChange={handleFileChange}
                          type="file"
                        />
                        <i className="fs-input-icon fa fa-clock" />
                      </div>
                    </div>
                  </div>

                  {/* Description */}
                  <div className="col-md-12">
                    <div className="form-group">
                      <label>Description</label>
                      <textarea
                        className="form-control"
                        name="description"
                        value={formData.description}
                        onChange={handleChange}
                        rows={3}
                        placeholder="Enter course description"
                        required
                      />
                    </div>
                  </div>

                  {/* Submit Buttons */}
                  <div className="col-lg-12 col-md-12">
                    <div className="text-left">
                      <button type="submit" className="site-button m-r5">
                        Publish Course
                      </button>
                      <button type="button" className="site-button outline-primary">
                        Save Draft
                      </button>
                    </div>
                  </div>
                </div>

                {/* Error or Success Messages */}
                {error && <p className="text-danger">{error}</p>}
                {success && <p className="text-success">{success}</p>}
              </form>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default EmpPostAJobPage;
