import { NavLink, useNavigate } from "react-router-dom";
import JobZImage from "../../../../common/jobz-img";
import { canRoute, candidate, empRoute, employer, publicUser } from "../../../../../globals/route-names";
import { useEffect, useState } from "react";

import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

import Cookies from "js-cookie";

function AfterLoginPage() {
    const navigate = useNavigate();
    const [canusername, setCanUsername] = useState("guest");
    const [empusername, setEmpUsername] = useState("admin");
    const [password, setPassword] = useState("12345");
    const [candidateError, setCandidateError] = useState("");
    const [employerError, setEmployerError] = useState("");

    useEffect(() => {
        setTimeout(() => {
            window.dispatchEvent(new Event("resize")); // Trigger the resize event to reinitialize Slick
        }, 100);
    }, []);

    const sliderSettings = {
        dots: true,
        infinite: true,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        autoplaySpeed: 2500,
        responsive: [
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1,
                },
            },
            {
                breakpoint: 600,
                settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1,
                },
            },
        ],
    };

    const sliderImages = [
        "images/login1.png",
        "images/login2.png",
        "images/login3.png",
        "images/login4.png",
    ];

    const handleCandidateLogin = async (event) => {
        event.preventDefault();
        setCandidateError("");

        if (!canusername || !password) {
            setCandidateError("Both username and password are required.");
            return;
        }

        try {
            const response = await fetch("http://localhost:8080/candidate-login", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({ Username: canusername, Password: password }),
            });

            const data = await response.json();

            if (response.ok) {
                Cookies.set("candidateId", data._id, { expires: 7 }); // Save candidate ID in cookies
                navigate(canRoute(candidate.DASHBOARD));
                window.location.reload(); // Refresh the page
            } else if (response.status === 401) {
                setCandidateError("Incorrect password. Please try again.");
            } else if (response.status === 404) {
                setCandidateError("User not found. Please check your username.");
            } else {
                setCandidateError(data.message || "Login failed. Please try again.");
            }
        } catch (error) {
            console.error("Error:", error);
            setCandidateError("An error occurred. Please try again later.");
        }
    };

    return (
        <>
            <div className="section-full popup-form">
                <div className="loginbg">
                    <div className="container" style={{ borderRadius: "40px" }}>
                        <div className="row popup-form-section">
                            {/* Left Side: Image Slider */}
                            <div className="col-xl-6 col-lg-6 col-md-6 col-12 popup-cards-left">
                                <Slider {...sliderSettings}>
                                    {sliderImages.map((src, index) => (
                                        <div key={index}>
                                            <JobZImage src={src} alt={`Slide ${index + 1}`} />
                                        </div>
                                    ))}
                                </Slider>
                            </div>

                            {/* Right Side: Login Form */}
                            <div className="col-xl-6 col-lg-6 col-md-6 col-12 popup-cards-right">
                                <div className="twm-log-reg-logo-head">
                                    <NavLink to={publicUser.HOME1}>
                                        <img
                                            src="/assets/images/skins-logo/logo-skin-6-ftr.png"
                                            alt=""
                                            className="logo"
                                        />
                                    </NavLink>
                                </div>
                                <div className="twm-log-reg-inner">
                                    <div className="twm-log-reg-head">
                                        <div className="twm-log-reg-logo">
                                            <span className="log-reg-form-title">Log In</span>
                                        </div>
                                    </div>
                                    {/* Candidate Login Form */}
                                    <form onSubmit={handleCandidateLogin}>
                                        <div className="row">
                                            <div className="col-lg-12">
                                                <div className="form-group mb-3">
                                                    <input
                                                        name="username"
                                                        type="text"
                                                        required
                                                        className="form-control"
                                                        placeholder="Username*"
                                                        onChange={(e) => setCanUsername(e.target.value)}
                                                    />
                                                </div>
                                            </div>
                                            <div className="col-lg-12">
                                                <div className="form-group mb-3">
                                                    <input
                                                        name="password"
                                                        type="password"
                                                        required
                                                        className="form-control"
                                                        placeholder="Password*"
                                                        onChange={(e) => setPassword(e.target.value)}
                                                    />
                                                </div>
                                            </div>
                                            {candidateError && (
                                                <div className="col-lg-12 text-danger mb-3">
                                                    {candidateError}
                                                </div>
                                            )}
                                            <div className="col-md-12">
                                                <button type="submit" className="site-button">
                                                    Log in
                                                </button>
                                            </div>
                                        </div>
                                    </form>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}

export default AfterLoginPage;
