import SectionJobsSidebar1 from "../../sections/jobs/sidebar/section-jobs-sidebar1";
import SectionJobsGrid from "../../sections/jobs/section-jobs-grid";
import SectionRecordsFilter from "../../sections/common/section-records-filter";
import { useEffect, useState } from "react";
import { loadScript } from "../../../../../globals/constants";

function JobsGridPage() {
  const _filterConfig = {
    prefix: "Showing",
    type: "jobs",
    total: "2,150",
    showRange: false,
    showingUpto: "",
  };

  const [perPage, setPerPage] = useState(10);

  useEffect(() => {
    loadScript("js/custom.js");
  }, []);
  const [filters, setFilters] = useState({
    category: "All Category",
    jobTitle: "",
    location: "",
    priceRange: ""
});

const [filteredJobs, setFilteredJobs] = useState([]);
const [jobs, setJobs] = useState([]);

useEffect(() => {
    // Fetching the jobs data from the API
    fetch("http://localhost:8080/post-job")
        .then((response) => response.json())
        .then((data) => setJobs(data))
        .catch((error) => console.error("Error fetching jobs:", error));
}, []);

useEffect(() => {
    // Filtering jobs based on the current filter state
    const filtered = jobs.filter((job) => {
        return (
            (filters.category === "All Category" || job.category === filters.category) &&
            (filters.jobTitle === "" || job.jobtitle.toLowerCase().includes(filters.jobTitle.toLowerCase())) &&
            (filters.location === "" || job.location.toLowerCase().includes(filters.location.toLowerCase())) &&
            (filters.priceRange === "" ||
                (filters.priceRange === "Below 50K" && parseInt(job.offeredsalary.replace(/[^\d.-]/g, "")) < 50000) ||
                (filters.priceRange === "50K-100K" && parseInt(job.offeredsalary.replace(/[^\d.-]/g, "")) >= 50000 && parseInt(job.offeredsalary.replace(/[^\d.-]/g, "")) <= 100000) ||
                (filters.priceRange === "100K-150K" && parseInt(job.offeredsalary.replace(/[^\d.-]/g, "")) > 100000 && parseInt(job.offeredsalary.replace(/[^\d.-]/g, "")) <= 150000) ||
                (filters.priceRange === "Above 150K" && parseInt(job.offeredsalary.replace(/[^\d.-]/g, "")) > 150000)
            )
        );
    });
    setFilteredJobs(filtered);
}, [filters, jobs]);

  return (
    <div className="section-full p-t120 p-b90 site-bg-white">
      <div className="container">
        <div className="row">
          <div className="col-lg-4 col-md-12 rightSidebar">
            <SectionJobsSidebar1 />
          </div>
          <div className="col-lg-8 col-md-12">
            {/* Filter Component */}
            {/* <SectionRecordsFilter
              _config={_filterConfig}
              onCandidatesPerPageChange={setPerPage}
            /> */}
            {/* Jobs Grid Component */}
            <SectionJobsGrid perPage={perPage} />
          </div>
        </div>
      </div>
    </div>
  );
}

export default JobsGridPage;
