import { NavLink } from "react-router-dom";
import { publicUser } from "../../../../../../globals/route-names";
import SectionSideAdvert from "./section-side-advert";

function SectionJobsSidebar1() {
    const [category, setCategory] = useState('All Category');
    const [jobTitle, setJobTitle] = useState('');
    const [location, setLocation] = useState('');
    const [priceRange, setPriceRange] = useS('');
    
    const handleFilterChange = () => {
        // Trigger the parent callback function when any filter changes
        onFilterChange({
            category,
            jobTitle,
            location,
            priceRange
        });
    };
    return (
        <>
       <div className="side-bar">
            <div className="sidebar-elements search-bx">
                <form>
                    {/* Category Filter */}
                    <div className="form-group mb-4">
                        <h4 className="section-head-small mb-4">Category</h4>
                        <select
                            className="wt-select-bar-large selectpicker"
                            value={category}
                            onChange={(e) => setCategory(e.target.value)}
                        >
                            <option>All Category</option>
                            <option>Web Designer</option>
                            <option>Developer</option>
                            <option>Accountant</option>
                        </select>
                    </div>
                    
                    {/* Job Title Filter */}
                    <div className="form-group mb-4">
                        <h4 className="section-head-small mb-4">Job Title</h4>
                        <input
                            type="text"
                            className="form-control"
                            placeholder="Job title"
                            value={jobTitle}
                            onChange={(e) => setJobTitle(e.target.value)}
                        />
                    </div>

                    {/* Location Filter */}
                    <div className="form-group mb-4">
                        <h4 className="section-head-small mb-4">Location</h4>
                        <input
                            type="text"
                            className="form-control"
                            placeholder="Search location"
                            value={location}
                            onChange={(e) => setLocation(e.target.value)}
                        />
                    </div>

                    {/* Price Range Filter */}
                    <div className="form-group mb-4">
                        <h4 className="section-head-small mb-4">Price Range</h4>
                        <select
                            className="wt-select-bar-large selectpicker"
                            value={priceRange}
                            onChange={(e) => setPriceRange(e.target.value)}
                        >
                            <option value="">Select Price Range</option>
                            <option value="Below 50K">Below 50K</option>
                            <option value="50K-100K">50K-100K</option>
                            <option value="100K-150K">100K-150K</option>
                            <option value="Above 150K">Above 150K</option>
                        </select>
                    </div>
                </form>
                <button className="btn btn-primary" onClick={handleFilterChange}>Apply Filters</button>
            </div>
        </div>
            <SectionSideAdvert />
        </>
    )
}

export default SectionJobsSidebar1;
