import React, { useEffect, useState } from "react";
import SectionPagination from "../common/section-pagination";

function SectionJobsGrid({ perPage, filters }) {
  const [jobs, setJobs] = useState([]); // Initialize as an empty array
  const [currentPage, setCurrentPage] = useState(1);

  useEffect(() => {
    const fetchJobs = async () => {
      try {
        const queryParams = new URLSearchParams(filters).toString();
        const response = await fetch(`http://localhost:8080/jobs/filter?${queryParams}`);
        const data = await response.json();

        // Ensure data is always an array
        if (Array.isArray(data)) {
          setJobs(data);
        } else {
          console.error("Expected an array but got:", data);
          setJobs([]); // Set to an empty array if data is not an array
        }
      } catch (error) {
        console.error("Error fetching jobs:", error);
        setJobs([]); // Set to an empty array in case of an error
      }
    };

    fetchJobs();
  }, [filters]);

  // Ensure jobs is always an array before slicing
  const indexOfLastJob = currentPage * perPage;
  const indexOfFirstJob = indexOfLastJob - perPage;
  const currentJobs = Array.isArray(jobs) ? jobs.slice(indexOfFirstJob, indexOfLastJob) : [];

  return (
    <>
      <div className="row">
        {currentJobs.map((job) => (
          <div className="col-lg-6 col-md-12 m-b30" key={job._id}>
            <div className="twm-jobs-grid-style1">
              <div className="twm-media">
                <img
                  src={`http://localhost:8080/${job.userId.companylogo}`}
                  alt={job.userId._id}
                />
              </div>
              <span className="twm-job-post-duration">
                {new Date(job.startDate).toLocaleDateString()}
              </span>
              <div className="twm-jobs-category green">
                <span className="twm-bg-sky">{job.jobtype}</span>
              </div>
              <div className="twm-mid-content">
                <h4>{job.jobcategory}</h4>
                <p>{job.location}</p>
                <a href={job.website} className="twm-job-websites site-text-primary">
                  {job.website}
                </a>
              </div>
              <div className="twm-right-content">
                <div className="twm-jobs-amount">
                  {job.offeredsalary} <span>/ Month</span>
                </div>
                <button className="btn btn-primary">Browse Job</button>
              </div>
            </div>
          </div>
        ))}
      </div>

      <SectionPagination
        totalItems={jobs.length}
        perPage={perPage}
        currentPage={currentPage}
        paginate={setCurrentPage}
      />
    </>
  );
}

export default SectionJobsGrid;
