import React, { useEffect, useState } from "react";
import SectionPagination from "../common/section-pagination";

function SectionJobsGrid({ perPage, filters }) {
  const [allJobs, setAllJobs] = useState([]); // Store all jobs fetched from the API
  const [filteredJobs, setFilteredJobs] = useState([]); // Store filtered jobs
  const [currentPage, setCurrentPage] = useState(1);

  // Fetch all jobs when the component mounts
  useEffect(() => {
    const fetchJobs = async () => {
      try {
        const response = await fetch("http://localhost:8080/post-job");
        const data = await response.json();

        // Ensure data is always an array and contains valid job objects
        if (Array.isArray(data)) {
          const validJobs = data.filter((job) => job && job.jobcategory); // Filter out invalid jobs
          setAllJobs(validJobs);
          setFilteredJobs(validJobs); // Initially, show all valid jobs
        } else {
          console.error("Expected an array but got:", data);
          setAllJobs([]);
          setFilteredJobs([]);
        }
      } catch (error) {
        console.error("Error fetching jobs:", error);
        setAllJobs([]);
        setFilteredJobs([]);
      }
    };

    fetchJobs();
  }, []);

  // Apply filters whenever the `filters` prop changes
  useEffect(() => {
    const applyFilters = () => {
      let filtered = allJobs;

      // Ensure `filtered` is an array before applying filters
      if (!Array.isArray(filtered)) {
        console.error("Expected an array but got:", filtered);
        setFilteredJobs([]);
        return;
      }

      if (filters.jobcategory) {
        filtered = filtered.filter(
          (job) =>
            job.jobcategory &&
            job.jobcategory.toLowerCase().includes(filters.jobcategory.toLowerCase())
        );
      }

      if (filters.offersalary) {
        filtered = filtered.filter(
          (job) => job.offeredsalary && job.offeredsalary >= parseFloat(filters.offersalary)
        );
      }

      if (filters.location) {
        filtered = filtered.filter(
          (job) =>
            job.location &&
            job.location.toLowerCase().includes(filters.location.toLowerCase())
        );
      }

      if (filters.datePosted) {
        const now = new Date();
        let startDate;

        switch (filters.datePosted) {
          case "last 24 hours":
            startDate = new Date(now.setDate(now.getDate() - 1));
            break;
          case "last 7 days":
            startDate = new Date(now.setDate(now.getDate() - 7));
            break;
          case "last 30 days":
            startDate = new Date(now.setDate(now.getDate() - 30));
            break;
          default:
            startDate = null;
        }

        if (startDate) {
          filtered = filtered.filter(
            (job) => job.startDate && new Date(job.startDate) >= startDate
          );
        }
      }

      setFilteredJobs(filtered);
    };

    applyFilters();
  }, [filters, allJobs]);

  // Pagination logic
  const indexOfLastJob = currentPage * perPage;
  const indexOfFirstJob = indexOfLastJob - perPage;
  const currentJobs = filteredJobs.slice(indexOfFirstJob, indexOfLastJob);

  return (
    <>
      <div className="row">
        {currentJobs.map((job) => (
          <div className="col-lg-6 col-md-12 m-b30" key={job._id}>
            <div className="twm-jobs-grid-style1">
              <div className="twm-media">
                <img
                  src={`http://localhost:8080/${job.userId.companylogo}`}
                  alt={job.userId._id}
                />
              </div>
              <span className="twm-job-post-duration">
                {new Date(job.startDate).toLocaleDateString()}
              </span>
              <div className="twm-jobs-category green">
                <span className="twm-bg-sky">{job.jobtype}</span>
              </div>
              <div className="twm-mid-content">
                <h4>{job.jobcategory}</h4>
                <p>{job.location}</p>
                <a href={job.website} className="twm-job-websites site-text-primary">
                  {job.website}
                </a>
              </div>
              <div className="twm-right-content">
                <div className="twm-jobs-amount">
                  {job.offeredsalary} <span>/ Month</span>
                </div>
                <button className="btn btn-primary">Browse Job</button>
              </div>
            </div>
          </div>
        ))}
      </div>

      <SectionPagination
        totalItems={filteredJobs.length}
        perPage={perPage}
        currentPage={currentPage}
        paginate={setCurrentPage}
      />
    </>
  );
}

export default SectionJobsGrid;
