import { useState, useEffect } from 'react';

function SectionJobLocation({ jobId }) {
  const [job, setJob] = useState(null);

  useEffect(() => {
    // Fetch job data from the API
    const fetchJobData = async () => {
      try {
        const response = await fetch(`/post-job/${jobId}`);
        const data = await response.json();
        setJob(data); // Set the fetched job data
      } catch (error) {
        console.error('Error fetching job data:', error);
      }
    };

    fetchJobData();
  }, [jobId]);

  if (!job) {
    return <p>Loading job details...</p>;
  }

  const { location } = job; // Get location from the job data
  const mapSrc = location
    ? `https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3304.8534521658976!2d${location.lng}!3d${location.lat}!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x80c2c6fd9829c6f3%3A0x6ecd11bcf4b0c23a!2s1363%20Sunset%20Blvd%2C%20Los%20Angeles%2C%20CA%2090026%2C%20USA`
    : '';

  return (
    <>
      <h4 className="twm-s-title">Location</h4>
      <div className="twm-m-map mb-5">
        <div className="twm-m-map-iframe">
          {location ? (
            <iframe
              height={310}
              src={mapSrc}
              title="Job Location"
              frameBorder="0"
              style={{ border: 0 }}
              allowFullScreen=""
              aria-hidden="false"
              tabIndex="0"
            />
          ) : (
            <p>No location available</p>
          )}
        </div>
      </div>
    </>
  );
}

export default SectionJobLocation;
